import React from 'react'
import EditPage from './EditPage';

const ProductsList = (props) => {

  const productsInfo = props.productsInfo;
  const setMainContent = props.setContent;
  const setPage = props.setPage;
  const updateProducts = props.updateProducts;

  const handleDeleteItem  = (id) => {
  const apiURL = `http://localhost:5001/alest-shop-fullstack/us-central1/webApi/api/products/${id}`;

    fetch(apiURL, {
      method: 'delete',
    });

    setTimeout(updateProducts(), 1000);
  };

  const handleEditItem = (product) => {

    setPage(<EditPage setContent={setMainContent} productsInfo={productsInfo}/>)
    setMainContent(prevState=>!prevState);
  }

  return (
    <section className='productslist'>
      {productsInfo.map((p) => {

        const {name, img, price, platform, genres} = p.data
        return (
          <article className='productcard' key={p.i}>
            <img src={img} alt={name}/>
            <p>Nome: {name}</p>
            <p>Preço: {price}</p>
            <p>Plataforma: {platform}</p>
            <p>Gênero: {genres}</p>
            <button 
              className='editproductbtn'
              onClick={()=>handleEditItem(p)}>
              Edit
            </button>
            <button 
              className='deleteproductbtn'
              onClick={()=>handleDeleteItem(p.i)}>
              delete
            </button>
          </article>
        )
      })}
    </section>
  );
}

export default ProductsList;
