import React, {useEffect, useState} from 'react';

const App = () => {

  const apiURL = 'http://localhost:5001/alest-shop-fullstack/us-central1/webApi/api/products';

  const [isLoading, setIsLoading] = useState(true);
  const [isError, setIsError] = useState(false);
  const [error, setError] = useState('');
  const [products, setProducts] = useState([]);

  const getAllProducts = async () => {
    const response = await (fetch(apiURL));
    const allProducts = await (response.json());
    console.log(allProducts);
  };

  useEffect(() => {
    getAllProducts();
    fetch(apiURL)
    .then(res => {
      if (res.status >= 200 && res.status <= 299) {
        console.log(res.json());
        return res.json();
      } else {
        setIsLoading(false);
        setIsError(true);
        throw new Error(res.statusText);
      }
    })
    .then((res) => {
      setIsLoading(false);
      setProducts(res.json());
    })
    .catch((error) => setError(error));
  }, []);

  if (isLoading) {
    return <h1>Carregando</h1>
  }

  if (isError) {
    return (
      <>
        <h1>Desculpa, algo deu errado.</h1>
        <p>Detalhes t√©cnicos: `${error}`</p>
      </>
    );
  }
  return (
    <>
      <section className='App'>
        {
          products.keys().length > 0 && products.map((p)=>p) 
        }
      </section>
    </>
  );
}

export default App;
