import React, {useState} from 'react';

const AddPage = (props) => {

  const setMainContent = props.setContent;
  const [name, setName] = useState('');
  const [img, setImg] = useState('');
  const [price, setPrice] = useState(0);
  const [platform, setPlatform] = useState([]);
  const [genres, setGenres] = useState([]);

  const handleSubmit = (e) => {

     const apiURL = `http://localhost:5001/alest-shop-fullstack/us-central1/webApi/api/products/${productID}`;

    e.preventDefault();

    const productData = {
      name: name,
      img: img,
      price: price,
      platform: platform.split(' '),
      genres: genres.split(' ')
    };

    console.log(name);
    console.log(productData);

    fetch(apiURL, {
      method: 'POST',
      mode: 'cors',
      headers: {'content-type': 'application/json'},
      body: JSON.stringify(productData)
    })
      .then(res => console.log(res.status))
      .catch((error) => console.log(error));

    setMainContent(prevState=>!prevState);
  };

  return (
    <section className='addProductSection'>
      <form onSubmit={(e) => handleSubmit(e)}>
        <button type='submit'>Enviar</button>
      </form>
    </section>
  );
};

export default AddPage;
