import React, {useState} from 'react';

const AddPage = (props) => {

  const setMainContent = props.setContent;
  const [name, setName] = useState('');
  const [img, setImg] = useState('');
  const [price, setPrice] = useState(0);
  const [platform, setPlatform] = useState([]);
  const [genres, setGenres] = useState([]);

  const handleSubmit = (e) => {

     const apiURL = `http://localhost:5001/alest-shop-fullstack/us-central1/webApi/api/products/`;

    e.preventDefault();

    const productData = {
      name: name,
      img: img,
      price: price,
      platform: platform.split(' '),
      genres: genres.split(' ')
    };

    console.log(name);
    console.log(productData);

    fetch(apiURL, {
      method: 'POST',
      mode: 'cors',
      headers: {'content-type': 'application/json'},
      body: JSON.stringify(productData)
    })
      .then(res => console.log(res.status))
      .catch((error) => console.log(error));

    setMainContent(prevState=>!prevState);
  };

  return (
    <section className='addProductSection'>
      <form onSubmit={(e) => handleSubmit(e)}>

        <article className='imgeditsection'>
          <img src={img} alt={name}/>
          <input
            type='text'
            placeholder='image url'
            onChange={(e) => setImg(e.target.value)}
            value={img || ""}/>
        </article>

        <article>

          <label htmlFor='name'>Nome: </label><br/>
          <input
            id='name'
            type='text'
            onChange={(e) => setName(e.target.value)}
            value={name}/>

          <br/>
          <label htmlFor='price'>Preço: </label><br/>
          <input
            id='price'
            type='number'
            onChange={(e) => setPrice(e.target.value)}
            value={price}/>

          <br/>
          <label htmlFor='platform'>Plataformas: </label><br/>
          <input
            id='platform'
            type='text'
            onChange={(e) => setPlatform(e.target.value)}
            value={platform}/>

          <br/>
          <label htmlFor='genres'>Gêneros: </label><br/>
          <input
            id='genres'
            type='text'
            onChange={(e) => setGenres(e.target.value)}
            value={genres}/>
        </article>
        <button type='submit'>Enviar</button>
      </form>
    </section>
  );
};

export default AddPage;
