import React, {useEffect, useState} from 'react';

const EditPage = (props) => {

  const setMainContent = props.setContent;
  const productsInfo = props.productsInfo;

  const [product, setProduct] = useState(...productsInfo);
  const [name, setName] = useState(product.data.name);
  const [img, setImg] = useState(product.data.img);
  const [price, setPrice] = useState(product.data.price);
  const [platform, setPlatform] = useState(product.data.platform);
  const [genres, setGenres] = useState(product.data.genres)

  useEffect(() => {
    setPlatform(() => {
      return (
        platform.map((p, i) => {
          if (i < Object.keys(platform).length) {
            return p + ','
          } else {
            return p
          }
        })
      );
    })
  }, []);

  const handleSubmit = (e) => {


    const productID = productsInfo[0].i;
    const apiURL = `http://localhost:5001/alest-shop-fullstack/us-central1/webApi/api/products/${productID}`;

    e.preventDefault();

    const productData = {
      name: name, 
      img: img,
      price: price, 
      platform: platform.split(','), 
      genres: genres.split(',')
    };

    console.log(name);
    console.log(productData);

    fetch(apiURL, {
      method: 'PUT',
      mode: 'cors',
      headers: {'content-type': 'application/json'},
      body: JSON.stringify(productData)
    })
      .then(res => console.log(res.status))
      .catch((error) => console.log(error));
  };

  const handleExit = () => {
    setMainContent(prevState => !prevState);
  };

  return (
    <section className='editproductsection'>
      <form onSubmit={handleSubmit}>
        <article className='imgeditsection'>
          <img src={img} alt={name}/>
          <input 
            type='text' 
            placeholder='image url'
            onChange={(e) => setImg(e.target.value)}
            value={img || ""}/>
        </article>

        <article>

          <label htmlFor='name'>Nome: </label><br/>
          <input 
            id='name' 
            type='text' 
            onChange={(e) => setName(e.target.value)}
            value={name}/>

          <br/>
          <label htmlFor='price'>Preço: </label><br/>
          <input 
            id='price' 
            type='number' 
            onChange={(e) => setPrice(e.target.value)}
            value={price}/>

          <br/>
          <label htmlFor='platform'>Plataformas: </label><br/>
          <input 
            id='platform' 
            type='text'
            onChange={(e) => setPlatform(e.target.value)}
            value={platform}/>

          <br/>
          <label htmlFor='genres'>Gêneros: </label><br/>
          <input 
            id='genres' 
            type='text'
            onChange={(e) => setGenres(e.target.value)}
            value={genres}/>

          <article>
            <button type='button' onClick={handleExit}>Sair sem salvar</button>
            <button type='submit'>Salvar</button>

          </article>
        </article>
      </form>
    </section>
  )
};

export default EditPage;
