import React, {useEffect, useState} from 'react';
import ProductsList from './components/ProductsList';

const App = () => {

  const apiURL = 'http://localhost:5001/alest-shop-fullstack/us-central1/webApi/api/products';

  const [isLoading, setIsLoading] = useState(true);
  const [isError, setIsError] = useState(false);
  const [error, setError] = useState('');
  const [mainContent, setMainContent] = useState(true);
  const [products, setProducts] = useState([]);

  const getAllProducts = async () => {
    await (fetch(apiURL)
      .then((res) => {
        if (res.status >=200 && res.status <=299) {
          return res.json()
        } else {
          setIsLoading(false);
          setIsError(true);
          setError(res.status + '-' + res.statusText);
          throw new Error(res.statusText);
        }
      })
      .then((res) => {
        setIsLoading(false);
        setProducts(res);
      })
      .catch((error) => console.log(error)));
  };

  useEffect(() => {
    getAllProducts();
  }, []);

  if (mainContent) {
    if (isLoading) {
      return (
        <section className='loadingsection'>
          <h1>Carregando, aguarde...</h1>
        </section>
      );  
    }

    if (isError) {
      return (
        <section className='errorsection'>
          <h1>Desculpa, algo deu errado.</h1>
          <p>Detalhes t√©cnicos: {error}</p>
        </section>
      );
    }
    return (
      <section className='App'>
        <ProductsList productsInfo={products}/>
      </section>
    );
  }
}

export default App;
