import React, {useState} from 'react';

const EditPage = (props) => {

  const setMainContent = props.setContent;
  const productsInfo = props.productsInfo;

  const [product, setProduct] = useState(...productsInfo);
  const [name, setName] = useState(product.data.name);
  const [price, setPrice] = useState(product.data.price);
  const [platform, setPlatform] = useState(product.data.platform);
  const [genres, setGenres] = useState(product.data.genres)

  const handleSubmit = (e) => {
    e.preventDefault();

    console.log({...product.data});

    setProduct((prevState) => {
      const newState = prevState.data = {
        ...product.data, 
        name: name , 
        price: price, 
        platform: platform, 
        genres: genres
      };

      console.log(prevState);

      return newState;
    });

    console.log(product.data);
  };

  const handleExit = () => {
    setMainContent(prevState => !prevState);
  };

  return (
    <section className='editproductsection'>
      <form onSubmit={handleSubmit}>
        <label htmlFor='name'>Nome: </label>
        <input 
          id='name' 
          type='text' 
          onChange={(e) => setName(e.target.value)}
          value={name}/>

        <br/>
        <label htmlFor='price'>Preço: </label>
        <input 
          id='price' 
          type='number' 
          onChange={(e) => setPrice(e.target.value)}
          value={price}/>

        <br/>
        <label htmlFor='platform'>Plataformas: </label>
        <input 
          id='platform' 
          type='text'
          onChange={(e) => setPlatform(e.target.value)}
          value={platform}/>

        <br/>
        <label htmlFor='genres'>Gêneros: </label>
        <input 
          id='genres' 
          type='text'
          onChange={(e) => setGenres(e.target.value)}
          value={genres}/>

        <article>
          <button type='button' onClick={handleExit}>Sair sem salvar</button>
          <button type='submit'>Salvar</button>

        </article>
      </form>
    </section>
  )
};

export default EditPage;
