import React from 'react'

const ProductsList = (props) => {

  const productsInfo = props.productsInfo;

  const handlerDeleteItem  = (id) => {
    const apiURL = `http://localhost:5001/alest-shop-fullstack/us-central1/webApi/api/products/${id}`;

    fetch(apiURL, {
      method: 'delete',
    })

    console.log('Deleted item!');
  };

  return (
    <section className='productslist'>
      {productsInfo.map((p) => {
        return (
          <article className='productcard' key={p.i}>
            <img src={p.data.img} alt={p.data.name}/>
            <p>Nome: {p.data.name}</p>
            <p>Preço: {p.data.price}</p>
            <p>Nota: {p.data.rating}</p>
            <p>Plataforma: {p.data.platform}</p>
            <p>Gênero: {p.data.genres}</p>
            <button 
              className='editproductbtn'>
              Edit
            </button>
            <button 
              className='deleteproductbtn'
              onClick={()=>handlerDeleteItem(p.i)}>
              delete
            </button>
          </article>
        )
      })}
    </section>
  );
}

export default ProductsList;
